#include <stdio.h>
#include <stdlib.h>

typedef struct book
{
    char loginNo[10];      //图书编号
    char name[20];         //书名
    char author[20];       //作者名
    char publisher[10];    //出版单位
    float price;           //价格
    int state;             //存在状态，0代表可以出借，1代表已经借出
    char borrowerName[10]; //借书人姓名
    int sex;               //借书人性别，0代表女，1代表男
    char stuNo[10];        //借书人学号
} Book;                    //图书信息结构体

// 1.	学生可以查看自己借阅的书籍、可以查阅书籍信息。
// 2.	管理可以查看所有学生借阅的书籍。
// 3.	学生归还图书之后，系统自动修改书籍的借阅信息。
// 4.	管理员可以增加、修改、删除图书。
// 5.	管理员可以注销学生信息。

int main()
{
    Book books[999];

    strcpy(books[0].loginNo,"123123");
    strcpy(books[0].name,"你好,世界");
    strcpy(books[0].author,"蒋若辉");
    strcpy(books[0].publisher,"电子工业");
    books[0].price=89;
    books[0].state=1;
    strcpy(books[0].borrowerName,"赵四");
    books[0].sex=1;
    strcpy(books[0].stuNo,"20200");

    int booksum = 1;

    // Hello();
    // printf("%d\n", booksum);
    // BookInput(books, &booksum);
    // printf("%d\n", booksum);
    // BookInput(books, &booksum);

    BookRead(books, booksum);

    return 0;
}

