int Menulogin(void)
{
    int choice;
    printf("\n\n\t\t\t\t\t\t\t--------------------------------欢迎使用图书管理系统--------------------------------\n\n");
    printf("\t\t\t\t\t\t\t-------------------------------------请选择身份-------------------------------------\n\n");
    printf("\n\n\t\t\t\t\t\t\t\t\t********\t   1：学生入口      \t********\t\t\n\n");
    printf("\n\n\t\t\t\t\t\t\t\t\t********\t   2：管理员入口    \t********\t\t\n\n");
    printf("\n\n\t\t\t\t\t\t\t------------------------------------------------------------------------------------\n\n");
    printf("请选择身份（1-2）：");
    scanf("%d",&choice);
    while(!(choice>=1&&choice<=2))
    {
        printf("输入有误，请重新输入（1,2）：");
        scanf("%d",&choice);
    }
    return choice;
}

